        const res = await axios.get('/data/GetLatestForecast.json');

        console.log("res.data:", res.data)
        latesForecastData.value = res.data;

        // 输出结果
        console.log("latesForecastData.value:", latesForecastData.value)

        // // 多加了{}必須在函式內加上return
        // normalValueRange.value = latesForecastData.value.filter((x) => { return x.Category === '雨量氣候正常值範圍' })
        // rainfallProbabilityForecast.value = latesForecastData.value.filter((x) => { return x.Category === '雨量機率預報' })


        // 只有一行時,箭頭函式可以省略{}及return
        normalValueRange.value = latesForecastData.value.filter((x) => x.Category === '雨量氣候正常值範圍');
        rainfallProbabilityForecast.value = latesForecastData.value.filter((x) => x.Category === '雨量機率預報');

